import React, { Component } from "react";

type SearchBarProps = {
  searchNews: (text: string) => void;
};

type SearchBarState = {
  searchString: string;
};

class SearchBar extends Component<SearchBarProps, SearchBarState> {
  static defaultProps = {
    searchNews: (text: string) => {}
  };

  state = {
    searchString: ""
  };

  searchNews = async (e: any) => {
    const { searchString } = this.state;

    if (e.key === "Enter" && searchString !== "") {
      e.preventDefault();
      e.stopPropagation();
      this.props.searchNews(searchString);
      console.log('You searched for: ' + searchString)

      const search = encodeURIComponent(searchString);
      const url = `http://localhost:4000/news-api?search=${search}`;
      const res = await fetch
      (url, 
        {
          method: 'POST',
          mode: 'cors',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(searchString)
        }
      )
      return res.json()
    }
  };

  onSearchTextChange = (e: any) => {
    this.setState({
      searchString: e.target.value.trim()
    });
  };

  render() {
    return (
      <div>
        <form>
          <div className="col-12 margin-bottom-1">
            <input
              className="search__bar col-6 padding-1"
              id="search"
              type="search"
              value={this.state.searchString}
              onChange={this.onSearchTextChange}
              onKeyPress={(e) => this.searchNews(e)}
              placeholder="Search"
            />
          </div>
        </form>
      </div>
    );
  }
}

export default SearchBar;
