import React, { useState } from 'react'
import './QueryButton.css';

function QueryButton() {
  const [ newsResponse, setNewsResponse ]= useState(null);

  function queryOnClick() {
    fetch(`http://localhost:4000/news-api`, {
      headers: { 'Content-Type': 'application/json' }
    })
      .then((response) => response.json())
      .then((result) => {
        console.log('result:', result);
        setNewsResponse(result);
      })
      .catch((ex) => {
        console.log('error:', ex);
      });
  }

  return (
    <div>
      {/* ! OMEGA ! THIS NEEDS TO BE REPLACED WITH AN INPUT COMPONENT */}
      <button className="query-button" onClick={queryOnClick}>
        Search
      </button>
      {/*  */}

      {newsResponse && newsResponse.articles ? (
        <table className="query-table">
          <tr>
            <th>Title</th>
            <th>Author</th>
            <th>Content</th>
            <th>URL</th>
          </tr>

          {newsResponse.articles.map((article: { 
            title: React.ReactNode;
            author: React.ReactNode;
            content: React.ReactNode;
            url: React.ReactNode;
          }) => (
            <tr>
              <td>{article.title}</td>
              <td>{article.author}</td>
              <td>{article.content}</td>
              <td>{article.url}</td>
            </tr>
          ))}
        </table>
      ) : null}
    </div>
  );
}

export default QueryButton;
