import React, { useState } from 'react'

function SearchButton() {
  const [ newsResponse, setNewsResponse ]= useState(null);

  function queryOnClick() {
    fetch(`http://localhost:4000/news-api`, {
      headers: { 'Content-Type': 'application/json' }
    })
      .then((response) => response.json())
      .then((result) => {
        console.log('result:', result);
        setNewsResponse(result);
      })
      .catch((ex) => {
        console.log('error:', ex);
      });
  }

  return (
    <div className="theme--white">
      <button className="search-button padding-1 margin-1" onClick={queryOnClick}>
        Search
      </button>

      {newsResponse && newsResponse.articles ? (
        <div className="results-container padding-2">
          {newsResponse.articles.map((article: { 
            title: React.ReactNode;
            author: React.ReactNode;
            content: React.ReactNode;
            url: React.ReactNode;
          }) => (
            <div className="article__container padding-2 margin-1 theme--white">
              <div className="article__title">{article.title}</div>
              <div className="article__author">{article.author}</div>
              <div className="article__content">{article.content}</div>
              <div className="article__url">Want to read more? Visit {article.url}</div>
            </div>
          ))}
        </div>
      ) : null}
    </div>
  );
}

export default SearchButton;
